sys_script_include.script:
var ScriptIncludeGetEmailAddress = Class.create();
// Extend the global.AbstractAjaxProcessor class
ScriptIncludeGetEmailAddress.prototype = Object.extendsObject(global.AbstractAjaxProcessor,{
	// Define the getEmail function.  
	// Create a GlideRecord for the User table.
	// Use the sysparm_userID passed from the client side to retrieve a record from the User table.
	// Return the email address for the requested record
	getEmail: function() {
		var userRecord = new GlideRecord("sys_user");
		//The syntax sysparm_ indicates to the Script Include that a value will be passed in from a client-side script at runtime. this.getParameter() is used to retrieve parameter values.
		userRecord.get(this.getParameter('sysparm_userID'));
		return userRecord.email + '';
	},
	type: 'ScriptIncludeGetEmailAddress'
});
